// ];

const AvailableMeals = () => {

    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading] = useState()

   useEffect(()=>{
     const fetchMeals = async ()=> {
       const response = await fetch('https://food-order-b6f65-default-rtdb.firebaseio.com/meals.json');
       const responseData = await response.json();

       let loadedMeals = [];

       for (const key in responseData){
         loadedMeals.push({
           id: key,
           name: responseData[key].name,
           description:responseData[key].description,
           price: responseData[key].price
         });
       }
       setMeals(loadedMeals)
     };

     fetchMeals();
   }, [])
    const mealsList = meals.map(
      meal => <MealItem key={meal.id} id={meal.id} name={meal.name} price={meal.price} description={meal.description}/>);
    return(
        <section className={classes.meals}>
          <Card>
            <ul>
              {mealsList}
            </ul>
          </Card>

        </section>
    )
}

export default AvailableMeals;
